        }}
      />
      <Tabs.Screen
        name="transactions"
        options={{
          title: 'Transactions',
        }}
      />
      <Tabs.Screen
        name="analysis"
        options={{
          title: 'Goals',
        }}
      />
    </Tabs>
  );
}

