import { TouchableOpacity, View } from 'react-native';
import TransactionListItem from './TransactionListItem';

type Category = { id?: string | number; name?: string };

// transaction shape in this project uses id as string and date as 'YYYY-MM-DD' or unix timestamp
type ProjectTransaction = {
  id: string | number;
  amount: number;
  date: string | number;
  description: string;
  type: 'income' | 'expense' | 'Income' | 'Expense';
  category_id?: string | number;
};

export default function TransactionList({
  transactions,
  categories,
  deleteTransaction,
  onEdit,
}: {
  categories: Category[];
  transactions: ProjectTransaction[];
  deleteTransaction: (id: string | number) => Promise<void>;
  onEdit?: (id: string | number) => void;
}) {
  return (
    <View style={{ gap: 15 }}>
      {transactions.map((transaction) => {
        const categoryForCurrentItem = categories.find(
          (category) => String(category.id) === String(transaction.category_id)
        );
        return (
          <TouchableOpacity
            key={String(transaction.id)}
            activeOpacity={0.7}
            onLongPress={() => deleteTransaction(transaction.id)}
            onPress={() => onEdit && onEdit(transaction.id)}
          >
            <TransactionListItem
              transaction={transaction as any}
              categoryInfo={categoryForCurrentItem}
            />
          </TouchableOpacity>
        );
      })}
    </View>
  );
}

src/auth/AuthContext.tsx.content:
import { createUserWithEmailAndPassword, onAuthStateChanged, signInWithEmailAndPassword, signOut } from 'firebase/auth';
import { doc, getDoc, setDoc, updateDoc } from 'firebase/firestore';
import React, { createContext, useContext, useEffect, useState } from 'react';
import { Alert } from 'react-native';
import { auth, db } from '../firebase';
import { UserProfile } from '../types';

interface AuthContextType {
  user: UserProfile | null;
  loading: boolean;
  login: (email: string, pass: string) => Promise<void>;
  register: (name: string, email: string, pass: string) => Promise<void>;
  logout: () => Promise<void>;
  updateUserCustomCategories?: (userId: string, type: 'expense' | 'income', categories: string[]) => Promise<void>;
}

const AuthContext = createContext<AuthContextType>({} as AuthContextType);

export const useAuth = () => useContext(AuthContext);

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<UserProfile | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (firebaseUser) => {
      try {
        if (firebaseUser) {
          const userDocRef = doc(db, 'users', firebaseUser.uid);
          const userDoc = await getDoc(userDocRef);
          if (userDoc.exists()) {
            setUser({
              uid: firebaseUser.uid,
              email: firebaseUser.email,
              name: userDoc.data().name,
              customCategories: userDoc.data().customCategories,
            });
          } else {
            // Fallback if user doc doesn't exist (e.g., new user just signed up)
            const newUser: UserProfile = {
              uid: firebaseUser.uid,
              email: firebaseUser.email,
              name: firebaseUser.email?.split('@')[0] || 'User',
              customCategories: { expense: [], income: [] },
            };
            await setDoc(userDocRef, newUser, { merge: true });
            setUser(newUser);
          }
        } else {
          setUser(null);
        }
      } catch (error) {
        console.error("Error fetching user profile:", error);
        setUser(null); // Ensure user is null on error
        Alert.alert("Error", "Failed to load user profile. Please try again.");
      } finally {
        setLoading(false);
      }
    });
    return unsubscribe;
  }, []);

  const login = async (email: string, pass: string) => {
    try {
      await signInWithEmailAndPassword(auth, email, pass);
    } catch (error: any) {
      Alert.alert('Login Failed', error.message);
      throw error;
    }
  };

  const register = async (name: string, email: string, pass: string) => {
    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, pass);
      const firebaseUser = userCredential.user;
      await setDoc(doc(db, 'users', firebaseUser.uid), {
        name: name,
        email: email,
        customCategories: { expense: [], income: [] },
      });
    } catch (error: any) {
      let errorMessage = 'An unexpected error occurred. Please try again.';
      if (error.code === 'auth/email-already-in-use') {
        errorMessage = 'This email address is already in use.';
      } else if (error.code === 'auth/weak-password') {
        errorMessage = 'The password is too weak. Please use at least 6 characters.';
      } else if (error.code === 'auth/invalid-email') {
        errorMessage = 'Please enter a valid email address.';
      }
      Alert.alert('Registration Failed', errorMessage);
      throw error;
    }
  };

  const logout = async () => {
    await signOut(auth);
  };

  const updateUserCustomCategories = async (
    userId: string,
    type: 'expense' | 'income',
    categories: string[]
  ) => {
    const userDocRef = doc(db, 'users', userId);
    await updateDoc(userDocRef, { [`customCategories.${type}`]: categories });
    setUser(prev => prev ? { ...prev, customCategories: { ...(prev.customCategories || {}), [type]: categories } } : prev);
  };

  return (
    <AuthContext.Provider value={{ user, loading, login, register, logout, updateUserCustomCategories }}>
      {children}
    </AuthContext.Provider>
  );
};

